DROP TABLE IF EXISTS `health_articles`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `health_articles` (
  `id` bigint NOT NULL COMMENT '主键，雪花 ID',
  `title` varchar(100) NOT NULL COMMENT '文章标题',
  `summary` varchar(500) DEFAULT NULL COMMENT '文章摘要',
  `content` text NOT NULL COMMENT '文章内容',
  `cover_image_url` varchar(255) DEFAULT NULL COMMENT '文章封面图片URL',
  `author_id` bigint NOT NULL COMMENT '作者ID，关联users.id，role=1（医生）',
  `author_name` varchar(32) NOT NULL COMMENT '作者姓名',
  `dept_id` int DEFAULT NULL COMMENT '科室ID',
  `dept_name` varchar(64) DEFAULT NULL COMMENT '科室名称',
  `view_count` int NOT NULL DEFAULT '0' COMMENT '浏览次数',
  `like_count` int NOT NULL DEFAULT '0' COMMENT '点赞次数',
  `is_top` tinyint NOT NULL DEFAULT '0' COMMENT '是否置顶：0 否 1 是',
  `status` tinyint NOT NULL DEFAULT '3' COMMENT '状态：0 草稿 1 已发布 2 已下架 3 审核中 4 未通过审核',
  `is_deleted` tinyint NOT NULL DEFAULT '0' COMMENT '逻辑删除：0 正常 1 已删',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_article_author` (`author_id`),
  KEY `idx_article_dept` (`dept_id`),
  KEY `idx_article_status` (`status`),
  KEY `idx_article_is_top` (`is_top`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='健康文章表';
