DROP TABLE IF EXISTS `health_video_comments`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `health_video_comments` (
  `id` bigint NOT NULL COMMENT '主键，雪花 ID',
  `video_id` bigint NOT NULL COMMENT '视频ID，关联health_videos.id',
  `user_id` bigint NOT NULL COMMENT '评论用户ID，关联users.id',
  `user_name` varchar(32) NOT NULL COMMENT '评论用户姓名',
  `user_avatar` varchar(255) DEFAULT NULL COMMENT '评论用户头像URL',
  `content` text NOT NULL COMMENT '评论内容',
  `like_count` int NOT NULL DEFAULT '0' COMMENT '点赞次数',
  `parent_id` bigint DEFAULT NULL COMMENT '父评论ID，用于回复功能',
  `reply_to_id` bigint DEFAULT NULL COMMENT '被回复用户ID',
  `reply_to_name` varchar(32) DEFAULT NULL COMMENT '被回复用户姓名',
  `status` tinyint NOT NULL DEFAULT '1' COMMENT '状态：0 隐藏 1 显示',
  `is_deleted` tinyint NOT NULL DEFAULT '0' COMMENT '逻辑删除：0 正常 1 已删',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_comment_video_id` (`video_id`),
  KEY `idx_comment_user_id` (`user_id`),
  KEY `idx_comment_parent_id` (`parent_id`),
  KEY `idx_comment_status` (`status`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='健康视频评论表';
/*!40101 SET character_set_client = @saved_cs_client */;

