CREATE TABLE `users` (
  `id` bigint NOT NULL COMMENT '主键，雪花 ID，全局唯一',
  `username` varchar(32) NOT NULL COMMENT '登录账号，业务唯一',
  `password_hash` char(60) NOT NULL COMMENT 'bcrypt/argon2 哈希，固定 60 位',
  `avatar_url` varchar(255) DEFAULT NULL COMMENT '头像 CDN 地址，列表页直接露出',
  `real_name` varchar(32) NOT NULL COMMENT '真实姓名',
  `role` tinyint NOT NULL COMMENT '身份：0 管理员 1 医生 2 患者',
  `phone` varchar(16) NOT NULL COMMENT '手机号，业务唯一',
  `email` varchar(64) DEFAULT NULL COMMENT '邮箱，可空，找回密码用',
  `gender` char(1) DEFAULT NULL COMMENT '性别 M 男 F 女 O 其他',
  `status` tinyint DEFAULT '0' COMMENT '账号状态：0 正常 1 锁定 2 未激活 3 已注销',
  `birth_date` date DEFAULT NULL COMMENT '出生日期，计算年龄',
  `is_deleted` tinyint DEFAULT '0' COMMENT '逻辑删除 0 正常 1 已删',
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_username` (`username`),
  UNIQUE KEY `uk_phone` (`phone`),
  KEY `idx_role` (`role`),
  KEY `idx_status` (`status`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='统一用户表，单表区分多角色';
